import sys

def main():
    input = sys.stdin.read
    data = input().split()
    T = int(data[0])
    index = 1
    results = []
    for _ in range(T):
        N = int(data[index])
        K = int(data[index+1])
        index += 2
        if N % 2 == 1:
            results.append("No")
        else:
            if N == 2:
                results.append("Yes")
            else:
                if K != N // 2:
                    results.append("Yes")
                else:
                    results.append("No")
    print('\n'.join(results))

if __name__ == "__main__":
    main()
